document.addEventListener("DOMContentLoaded",function(){setTimeout(function(){document.body.classList.remove("is-fading-in")},500);for(var t=document.querySelectorAll('a[href^="/"]'),e=0;e<t.length;e++)t[e].addEventListener("click",function(t){if(!(t.shiftKey||t.ctrlKey||t.altKey||t.metaKey)){t.preventDefault(),document.body.classList.add("is-fading-out");var e=this.href;setTimeout(function(){window.location=e},500)}});document.getElementById("js-mobile-nav-toggle").addEventListener("click",function(){this.classList.toggle("is-active"),this.nextSibling.classList.toggle("is-active")})});